package Lecture8;

public class BitMasking {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       
		System.out.println(2&3);
		System.out.println(1|2);
		
		System.out.println(2^3);
		
		
		int num = 16;
		int x = num<<3;
		
		System.out.println(x);
		
		x=num>>2;
		System.out.println(x);
	}

}
